import React, {useState} from 'react';
import { Draggable } from 'react-beautiful-dnd';

import { makeStyles } from '@material-ui/core/styles';
import clsx from 'clsx';
import {Card, CardMedia, CardContent, Collapse, Typography, IconButton} from '@material-ui/core';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';

import Accordion from 'react-bootstrap/Accordion';
//import Card from 'react-bootstrap/Card';

const useStyles = makeStyles((theme) => ({
    root: {
      minWidth: 370,
      maxWidth: 370,
      margin: 2,
    },
    header: {
      display: 'flex',
    },
    content: {
      flex: '1 0 auto',
    },
    cover: {
      width: "30%",
    },
    media: {
      height: 0,
      paddingTop: '56.25%', // 16:9
    },
    expand: {
      "&:hover": {
        backgroundColor: "transparent"
      },
      transform: 'rotate(0deg)',
      marginLeft: 'auto',
      transition: theme.transitions.create('transform', {
        duration: theme.transitions.duration.shortest,
      }),
    },
    expandOpen: {
      transform: 'rotate(180deg)',
    },
  }));


function Item(props) {

    const {place, index} = props;
    //const placeImgUrl = "/place/photo?maxheight=400&photoreference=" + place.photoRef + "&key=" + process.env.REACT_APP_GOOGLE_PLACES_API_KEY; 

    const classes = useStyles();
    const [expanded, setExpanded] = React.useState(false);

    const handleExpandClick = () => {
        setExpanded(!expanded);
    };

    return (
        <Draggable draggableId={place.id} 
            index={index}
        >
            {(provided) => (
                <Card 
                    className={classes.root}
                    ref={provided.innerRef}
                    {...provided.draggableProps}
                    {...provided.dragHandleProps}
                >
                    <div className={classes.header} onClick={handleExpandClick}>
                        <Typography>
                            {index+1}
                        </Typography>
                        <CardMedia
                        className={classes.cover}
                        image="https://i.imgur.com/zbBglmB.jpg"
                        title="Live from space album cover"
                        />
                        <div>
                        <CardContent className={classes.content}>
                            <Typography>
                            {place.content}
                            </Typography>
                            <Typography>
                            {place.rating}
                            </Typography>
                        </CardContent>
                        </div>
                        <IconButton
                        className={clsx(classes.expand, {
                            [classes.expandOpen]: expanded,
                        })}
                        onClick={handleExpandClick}
                        disableRipple="true"
                        disableFocusRipple="true"
                        aria-expanded={expanded}
                        aria-label="show more"
                        >
                        <ExpandMoreIcon />
                        </IconButton>
                    </div>
                    <Collapse in={expanded} timeout="auto" unmountOnExit>
                        <CardMedia
                        className={classes.media}
                        image="https://i.imgur.com/zbBglmB.jpg"
                        title="Paella dish"
                        />
                        <CardContent>
                        <Typography variant="body2" color="textSecondary" component="p">
                            This impressive paella is a perfect party dish and a fun meal to cook together with your
                            guests. Add 1 cup of frozen peas along with the mussels, if you like.
                        </Typography>
                        </CardContent>
                    </Collapse>
                </Card>
            )}
        </Draggable>
    );
}

export default Item;

