import React, { useContext } from 'react';
import { Link } from 'react-router-dom';
import { useQuery } from '@apollo/react-hooks';
import gql from 'graphql-tag';

import { AuthContext } from '../Store/AuthContext';

import NavigateNextIcon from '@material-ui/icons/NavigateNext';
import {Card, CardMedia, CardContent, Typography, IconButton, makeStyles} from '@material-ui/core';

const useStyles = makeStyles({
    root: {
        minWidth: 370,
        maxWidth: 370,
        margin: 5,
        display: 'flex',
    },
    headerThumbnail: {
        width: 100,
    },
    headerTitle: {
        flex: '1 0 auto',
    }
})

function Itineraries() {

    const { authState } = useContext(AuthContext);
    const classes = useStyles();

    const {
        loading,
        data: { getUserItineraries : itineraries } = {}
    } = useQuery(GET_USER_ITINERARIES, {
        variables: {
            userId: authState.user.id
        }
    });

    console.log(itineraries);
    let itineraryCards;
    if (itineraries) {
        itineraryCards = itineraries.map((itinerary) => {
        return (
            <Link to={`/itinerary/${itinerary.id}`} key={itinerary.id}>
                <Card className={classes.root}>
                    <CardMedia
                        className={classes.headerThumbnail}
                        image="https://i.imgur.com/zbBglmB.jpg"
                    />
                    <div>
                        <CardContent className={classes.headerTitle}>
                            <Typography>
                                {itinerary.city}
                            </Typography>
                            <Typography>
                                {itinerary.dayPlans.length} Days
                            </Typography>
                        </CardContent>
                    </div>
                    <IconButton
                        disableRipple={true}
                        disableFocusRipple={true}
                    >
                        <NavigateNextIcon />
                    </IconButton>
                </Card>
            </Link>
        )})
    }

    return (
        <div>
            <h1>Itineraries</h1>
            {itineraryCards}
        </div>
    );
}

const GET_USER_ITINERARIES = gql`
    query getUserItineraries(
        $userId: ID!
    ){
        getUserItineraries(
            userId: $userId
        ){
            id
            city
            dayPlans{
                placeIds
            }
            createdAt
            user
            username
        }
    }
`

