import React, { useState, useContext } from 'react';

import { makeStyles } from '@material-ui/core/styles';
import {TextField, MenuItem, Button} from '@material-ui/core/';
import FlightIcon from '@material-ui/icons/Flight';

import { PlaceContext } from '../Store/PlaceContext';
import { fetchCategories } from '../Services/googlePlaceApi'

const useStyles = makeStyles({
    textField: {
        marginRight: 5,
        width: '25ch',
    },
    submitButton: {
        height: 53,
    },
    root: {
        paddingTop: 20,
        paddingBottom: 40,
    }
})

function Form() {



    const { placeState, dispatch } = useContext(PlaceContext);

    const [days, setDays] = useState(placeState.days);
    const [location, setLocation] = useState(placeState.location);

    console.log('form has been re-rendered: ', days, location)

    const placeTypes = ['Restaurants', "Hotels", "Tourist+attraction"];

    const fetchPlaces = async () => {

        fetchCategories(placeTypes, location, dispatch)
        // const googlePlacesApi = process.env.REACT_APP_GOOGLE_PLACES_API_KEY;

        // for (var i = 0; i < placeTypes.length; i++){

        //     // https://stackoverflow.com/questions/43262121/trying-to-use-fetch-and-pass-in-mode-no-cors 
        //     // added proxy in package.json "proxy": "https://maps.googleapis.com/maps/api"

        //     const response = await fetch(`/place/textsearch/json?query=best+${placeTypes[i]}+${location}&key=${googlePlacesApi}`)
        //     const extraSuggestions = await response.json();

        //     dispatch({ type:"LOAD_ITINERARY", payload:{extraSuggestions, placeType: placeTypes[i]}})
        // }

    }


    const submitHandler = async () => {
        dispatch({ type:"CLEAR_STATE" })
        dispatch({ type:"LOAD_DAYBOARDS", payload:{numberOfDays: days, city: location}})
        fetchPlaces();
        // let compiledData = await fetchPlaces();
        // dispatch({ type:'SHOW_PLACES', search: {compiledData, days, location}});

    };

    const classes = useStyles();

    return (
        <div className={classes.root}>
            <TextField
            className={classes.textField}
            id="location"
            select
            label="Select City"
            value={location}
            onChange={(e) => setLocation(e.target.value)}
            variant="outlined"
            >
                <MenuItem value="Berlin">Berlin</MenuItem>
                <MenuItem value="Zurich">Zurich</MenuItem>
                <MenuItem value="New+York">New York</MenuItem>
            </TextField>
            <TextField 
                className={classes.textField}
                label="Days" 
                value={days} 
                variant="outlined" 
                onChange={(e) => setDays(e.target.value)}
            />
            <Button 
                variant="outlined" 
                className={classes.submitButton}
                onClick={submitHandler}
                startIcon={<FlightIcon />}
            >
                Submit
            </Button>
        </div>
    );
}

export default Form;
