import React, { useContext, useState } from 'react';
import { DragDropContext } from 'react-beautiful-dnd';

import initialData from '../Store/initial-data';
import Column from './column';

import { PlaceContext } from '../Store/PlaceContext';

function Board() {

    const { contextState, dispatch } = useContext(PlaceContext);
    const [type, setType] = useState('Restaurants');
    const [days, setDays] = useState(0);
    const [location, setLocation] = useState('');

    const [state, setState] = React.useState(initialData);

    const onDragEnd = result => {
        const { destination, source, draggableId } = result;

        if (!destination) {
            return;
        }

        if (
            destination.droppableId === source.droppableId &&
            destination.index === source.index
        ) {
            return;
        }

        const start = state.columns[source.droppableId];
        const finish = state.columns[destination.droppableId];

        //if moving within the same column
        if (start === finish) {
            const column = state.columns[source.droppableId];
            const newplaceIds = Array.from(column.placeIds);
            newplaceIds.splice(source.index, 1);
            newplaceIds.splice(destination.index, 0, draggableId);

            const newColumn = {
                ...column,
                placeIds: newplaceIds,
            };

            const newState = {
                ...state,
                columns: {
                    ...state.columns,
                    [newColumn.id]: newColumn,
                },
            };

            setState(newState);
            return;
        }

        //moving from one list to another
        const startplaceIds = Array.from(start.placeIds);
        startplaceIds.splice(source.index, 1);
        const newStart = {
            ...start,
            placeIds: startplaceIds,
        };

        const finishplaceIds = Array.from(finish.placeIds);
        finishplaceIds.splice(destination.index, 0, draggableId);
        const newFinish = {
            ...finish,
            placeIds: finishplaceIds,
        };


        const newOrder = {
            ...state,
            columns: {
                ...state.columns,
                [newStart.id]: newStart,
                [newFinish.id]: newFinish,
            },
        };

        setState(newOrder);
        dispatch({ type:'CHANGE_ORDER', order: {newOrder}});
    };

    const changeHandler = (e) => {
        setState({
            ...state,
            [e.target.id]: e.target.value
        })
    };

    const clickHandler = async () => {

        console.log(contextState);

        // https://stackoverflow.com/questions/43262121/trying-to-use-fetch-and-pass-in-mode-no-cors 
        // added proxy in package.json "proxy": "https://maps.googleapis.com/maps/api"
        const googlePlacesApi = process.env.REACT_APP_GOOGLE_PLACES_API_KEY;
        const response = await fetch(`/place/textsearch/json?query=${type}+${location}&key=${googlePlacesApi}`)
        const data = await response.json();

        dispatch({ type:'SHOW_PLACES', search: {data, days}});

    };

    return contextState ? (
        <div>
            <div>
                <select className="select-css" id='type' value={type} onChange={(e) => setType(e.target.value)}>
                    <option value="Restaurants">Restaurants</option>
                    <option value="Hotels">Hotels</option>
                    <option value="Tourist+attraction">Tourist+attraction</option>
                </select>
                <input id='place' placeholder='City' type="text" value={location} onChange={(e) => setLocation(e.target.value)}/>
                <input id='number' placeholder='No. of Days' type="text" value={days} onChange={(e) => setDays(e.target.value)}/>
                <button type='submit' onClick={clickHandler}>Submit</button>
            </div>
            <DragDropContext onDragEnd={onDragEnd}>
                <div className='container'>
                    {contextState.columnOrder.map(columnId => {
                        const column = contextState.columns[columnId];
                        const places = column.placeIds.map(placeId => 
                            contextState.places[placeId]
                        );

                        return <Column key={column.id} column={column} places={places}/>
                    })}
                </div>
                <div className='container'>
                    {contextState.dataColumn.map(columnId => {
                        const column = contextState.columns[columnId];
                        const places = column.placeIds.map(placeId => 
                            contextState.places[placeId]
                        );

                        return <Column key={column.id} column={column} places={places}/>
                    })}
                </div>
            </DragDropContext>
        </div>

    ) : (<div><p>hello</p></div>);

}

export default Board;
