import React from 'react';
import { Draggable } from 'react-beautiful-dnd';

const item = (props) => {

    const {task, index} = props;
    return (
        <Draggable draggableId={task.id} 
            index={index}
        >
            {(provided) => (
                <div className='item'
                    ref={provided.innerRef}
                    {...provided.draggableProps}
                    {...provided.dragHandleProps}
                >
                    {task.content}
                    {task.rating}
                </div>
            )}
        </Draggable>
    );
}

export default item;

