export const placeReducer = async (state, action) => {
    switch (action.type) {
        case 'SHOW_PLACES':
            loadPlaces(action.search.type, action.search.location);
            return {
                ...state,
                number: action.place.columnsCount,
            }
        default:
            return state;
    }
}

const loadPlaces = async (type, location) => {
    const googlePlacesApi = process.env.REACT_APP_GOOGLE_PLACES_API_KEY;
    const response = await fetch(`/place/textsearch/json?query=${type}+${location}&key=${googlePlacesApi}`)
    const data = await response.json();
    console.log(data.results)
}
