export const placeReducer = async (state, action) => {
    switch (action.type) {
        case 'SHOW_PLACES':
            const places = await loadPlaces(action.search.type, action.search.location, action.search.columnsCount);
            console.log(places);
            return {
                ...state,
            }
        default:
            return state;
    }
}

const loadPlaces = async (type, location, columnsCount) => {

    var columns = {};
    var columnOrder = [];

    for (var i = 0; i < columnsCount; i++) {
        var dataObject = {};
        dataObject['id'] = `column-${i}`;
        dataObject['title'] = `Day ${i + 1}`;
        dataObject['placeIds'] = [];

        columns[`column-${i}`] = dataObject;
        columnOrder.push(`column-${i}`);
    }

    const googlePlacesApi = process.env.REACT_APP_GOOGLE_PLACES_API_KEY;
    const response = await fetch(`/place/textsearch/json?query=${type}+${location}&key=${googlePlacesApi}`)
    const data = await response.json();
    console.log(data.results)

    var placesFetched = {};
    var placeIds = [];

    for (var j = 0; j < data.results.length; j++) {

        //object structure of place card is set here

        var placeObject = {};
        placeObject['id'] = `place-${j}`;
        placeObject['content'] = data.results[j].name;
        placeObject['rating'] = data.results[j].rating;
        placeObject['photoRef'] = data.results[j].photos ? data.results[j].photos[0].photo_reference : "0";
        //console.log(placeObject);
        placeIds.push(`place-${j}`);
        placesFetched[`place-${j}`] = placeObject;

    }

    columns['data-1'] = {
        ...state.columns['data-1'],
        placeIds: placeIds
    }

    const newState = {
        ...state,
        places: placesFetched,
        columns: {
            ...state.columns,
            ...columns,
        },
        columnOrder: [...columnOrder],
    }

    return newState;
}
