import React, {useContext} from 'react';
import { Draggable } from 'react-beautiful-dnd';

import Accordion from 'react-bootstrap/Accordion';
import Card from 'react-bootstrap/Card';

import { PlaceContext } from '../Store/PlaceContext'


function Item(props) {

    const { contextState, dispatch } = useContext(PlaceContext);
    const {place, index} = props;
    //const placeImgUrl = "/place/photo?maxheight=400&photoreference=" + place.photoRef + "&key=" + process.env.REACT_APP_GOOGLE_PLACES_API_KEY; 

    const onHoverHandler = () => {
        const newState = {
            ...contextState,
            places: {
                ...contextState.places,
                [place.id]: {
                    ...contextState.places[`${place.id}`],
                    color: 'red'
                }
            }
        }
       console.log(contextState.places[`${place.id}`].color);
       console.log(newState);
    }

    return (
        <Draggable draggableId={place.id} 
            index={index}
        >
            {(provided) => (
                    <Accordion
                        className="item"
                        ref={provided.innerRef}
                        {...provided.draggableProps}
                        {...provided.dragHandleProps}
                        onMouseEnter={onHoverHandler}
                    >
                        <Card>
                            <Accordion.Toggle as={Card.Header} eventKey="0">
                                {place.content}
                            </Accordion.Toggle>
                            <Accordion.Collapse eventKey="0">
                                <Card.Body 
                                    className="card-body"
                                >
                                    <p>{place.rating}</p>
                                    <p>Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur. Excepteur sint occaecat cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id est laborum.</p>
                                </Card.Body>
                            </Accordion.Collapse>
                        </Card>
                    </Accordion>
            )}
        </Draggable>
    );
}

export default Item;

