export const placeReducer = (state, action) => {
    switch (action.type) {
        case 'SHOW_PLACES':
            const { compiledData, days, location } = action.search;
            const placesLoaded = loadPlaces(state, compiledData, days, location);
            return placesLoaded;
        case 'ADD_EXTRA_DAY':
            const extraDay = addExtraDay(state);
            return extraDay;
        case 'CHANGE_ORDER':
            const { newOrder } = action.order;
            return newOrder;
        case 'LOAD_ITINERARY':
            const { itinerary } = action.payload
            console.log("from reducer:", itinerary);
            loadItinerary(itinerary)
            return state
        default:
            return state;
    }
}

const loadItinerary = async ( itinerary ) => {

    const googlePlacesApi = process.env.REACT_APP_GOOGLE_PLACES_API_KEY;
    const dayPlans = itinerary.dayPlans;

    for(var i = 0; i < dayPlans.length; i++) {
        const placeIds = dayPlans[i].placeIds
        for(var j = 0; j < placeIds.length; j++){
            const response = await fetch(`/place/details/json?placeid=${placeIds[j]}&key=${googlePlacesApi}`)
            const data = await response.json();
            console.log(data);
            let placeObject = {};
            placeObject['id'] = placeIds[j]
            placeObject['content'] = data.result.name;
            placeObject['rating'] = data.result.rating;
            placeObject['photoRef'] = data.result.photos ? data.result.photos[0].photo_reference : "0";
            placeObject['location'] = data.result.geometry.location;

        }
    }
}

const addExtraDay = (state) => {

    const nextColumnIndex = state.dayBoards.length;
    const nextColumn = {
        id: `day-${nextColumnIndex}`, 
        title: `Day ${nextColumnIndex + 1}`, 
        placeIds: []
    }

    const newState = {
        ...state,
        days: nextColumnIndex + 1,
        columns: {
            ...state.columns,
            [`day-${nextColumnIndex}`]: nextColumn
        },
        dayBoards: [...state.dayBoards, `day-${nextColumnIndex}`],
    }

    console.log(newState);
    return newState
}

const loadPlaces = (state, compiledData, days, location) => {

    let columns = {};
    let dayBoards = [];
    let categoryBoards = [];

    for (var i = 0; i < days; i++) {
        let dataObject = {};
        dataObject['id'] = `day-${i}`;
        dataObject['title'] = `Day ${i + 1}`;
        dataObject['placeIds'] = [];

        columns[`day-${i}`] = dataObject;
        dayBoards.push(`day-${i}`);
    }

    let placesFetched = {};
    const placeTypes = ['Restaurants', "Hotels", "Tourist+attraction"];

    for (var c = 0; c < compiledData.length; c++){

        let data = compiledData[c];
        let placeIds = [];

        for (var j = 0; j < data.results.length; j++) {

            //object structure of place card is set here

            var placeObject = {};
            placeObject['id'] = data.results[j].place_id;
            placeObject['content'] = data.results[j].name;
            placeObject['rating'] = data.results[j].rating;
            placeObject['photoRef'] = data.results[j].photos ? data.results[j].photos[0].photo_reference : "0";
            placeObject['location'] = data.results[j].geometry.location;

            //console.log(placeObject);
            placeIds.push(data.results[j].place_id);
            placesFetched[data.results[j].place_id] = placeObject;

        }

        columns[`category-${c+1}`] = {
            id: `category-${c+1}`,
            title: `${placeTypes[c]}`,
            placeIds: placeIds
        }

        categoryBoards.push(`category-${c+1}`);

    } 

    const newState = {
        ...state,
        days: days,
        location: location,
        places: placesFetched,
        columns: {
            ...state.columns,
            ...columns,
        },
        dayBoards: [...dayBoards],
        categoryBoards: [...categoryBoards],
    }

    // console.log(newState);
    return newState;
}
