import React, { useContext, useEffect } from 'react';
import { DragDropContext } from 'react-beautiful-dnd';
import { useQuery, useMutation } from '@apollo/react-hooks';
import gql from 'graphql-tag';

import DayBoard from './dayBoard';
import CategoryBoard from './categoryBoard';
import Form from './form';

import { PlaceContext } from '../Store/PlaceContext';

function Page(props) {

    const { placeState, dispatch } = useContext(PlaceContext);

    const itineraryId = props.match.params.itineraryId;
    const { data } = useQuery(GET_ITINERARY, {
        variables: {itineraryId}
    })
    useEffect(async() => {
        if(data){
            const googlePlacesApi = process.env.REACT_APP_GOOGLE_PLACES_API_KEY;
            const dayPlans = data.getItinerary.dayPlans;
            for(var i = 0; i < dayPlans.length; i++) {
                const placeIds = dayPlans[i].placeIds
                //columns[`day-${i}`].placeIds = placeIds
                for(var j = 0; j < placeIds.length; j++){
                    const response = await fetch(`/place/details/json?placeid=${placeIds[j]}&key=${googlePlacesApi}`)
                    const data = await response.json();

                    let placeObject = {};
                    placeObject['id'] = placeIds[j]
                    placeObject['content'] = data.result.name;
                    placeObject['rating'] = data.result.rating;
                    placeObject['photoRef'] = data.result.photos ? data.result.photos[0].photo_reference : "0";
                    placeObject['location'] = data.result.geometry.location;

                    console.log(placeObject);
                    //placesFetched[placeIds[j]] = placeObject;
                }
            }
            //dispatch({ type:"LOAD_ITINERARY", payload:{itinerary: data.getItinerary}})
        }
    }, [data])


    const onDragEnd = result => {
        const { destination, source, draggableId } = result;

        if (!destination) {
            return;
        }

        if (
            destination.droppableId === source.droppableId &&
            destination.index === source.index
        ) {
            return;
        }

        const start = placeState.columns[source.droppableId];
        const finish = placeState.columns[destination.droppableId];

        //if moving within the same column
        if (start === finish) {
            const column = placeState.columns[source.droppableId];
            const newplaceIds = Array.from(column.placeIds);
            newplaceIds.splice(source.index, 1);
            newplaceIds.splice(destination.index, 0, draggableId);

            const newColumn = {
                ...column,
                placeIds: newplaceIds,
            };

            const newOrder = {
                ...placeState,
                columns: {
                    ...placeState.columns,
                    [newColumn.id]: newColumn,
                },
            };
            console.log(newOrder);
            dispatch({ type:'CHANGE_ORDER', order: {newOrder}});
            return;
        }

        //moving from one list to another
        const startplaceIds = Array.from(start.placeIds);
        startplaceIds.splice(source.index, 1);
        const newStart = {
            ...start,
            placeIds: startplaceIds,
        };

        const finishplaceIds = Array.from(finish.placeIds);
        finishplaceIds.splice(destination.index, 0, draggableId);
        const newFinish = {
            ...finish,
            placeIds: finishplaceIds,
        };


        const newOrder = {
            ...placeState,
            columns: {
                ...placeState.columns,
                [newStart.id]: newStart,
                [newFinish.id]: newFinish,
            },
        };

        console.log(newOrder);
        dispatch({ type:'CHANGE_ORDER', order: {newOrder}});
    };

    let itinerary = [];

    const [submitItinerary] = useMutation(SUBMIT_ITINERARY, {
        // result is the second parameter!
        update(_, result){
            console.log(result.data);
        },
        onError(err){
            console.log(err)
        },
        variables: {
            dayPlans: itinerary,
            city: placeState.location
        }
    })

    const addExtraDay = () => {
        dispatch({type:'ADD_EXTRA_DAY'});
    }

    const saveItinerary = () => {
        const days = placeState.dayBoards;
        for (var i = 0; i < days.length; i ++){
            itinerary.push({
                placeIds: placeState.columns[days[i]].placeIds
            });

        }
        console.log(itinerary);
        submitItinerary();
    }

    console.log(placeState);

    return (
        <div>
            <Form/>
            <DragDropContext onDragEnd={onDragEnd}>
                <div className='day-boards-container'>
                    {placeState.dayBoards.map(columnId => {
                        const column = placeState.columns[columnId];
                        const places = column.placeIds.map(placeId => 
                            placeState.places[placeId]
                        );

                        return <DayBoard key={column.id} column={column} places={places}/>
                    })}
                    {placeState.dayBoards.length > 0 ? <button className="extra-day" onClick={addExtraDay}>+</button> : ""}

                </div>
                <button onClick={saveItinerary}>Save Itinerary</button>
                <div className='place-boards-container'>
                    {placeState.categoryBoards.map(columnId => {
                        const column = placeState.columns[columnId];
                        const places = column.placeIds.map(placeId => 
                            placeState.places[placeId]
                        );

                        return <CategoryBoard key={column.id} column={column} places={places}/>
                    })}
                </div>
            </DragDropContext>
        </div>

    );

}

const SUBMIT_ITINERARY =  gql`
    mutation submitItinerary(
        $dayPlans: [DayPlanInput]!
        $city: String!
    ){
        submitItinerary(
            dayPlans: $dayPlans
            city: $city
        ){
            id
            city
            dayPlans{
                placeIds
            }
            createdAt
            user
            username
        }
    }
`

const GET_ITINERARY = gql`
    query getItinerary(
        $itineraryId: ID!
    ){
        getItinerary(
            itineraryId: $itineraryId
        ){
            id
            city
            dayPlans{
                placeIds
            }
            createdAt
            user
            username 
        }
    }
`

export default Page;
