import initialData from './initial-data';

export const placeReducer = (state, action) => {
    switch (action.type) {
        case 'SHOW_PLACES':
            const { compiledData, days, location } = action.search;
            const placesLoaded = loadPlaces(state, compiledData, days, location);
            return placesLoaded;
        case 'ADD_EXTRA_DAY':
            const extraDay = addExtraDay(state);
            return extraDay;
        case 'CHANGE_ORDER':
            const { newOrder } = action.order;
            return newOrder;
        case 'LOAD_DAYBOARDS':
            console.log('loading day boards')
            console.log('before dayboards loaded places length: ', Object.keys(state.places).length)
            const { numberOfDays, city } = action.payload
            const dayBoardsLoaded = loadDayBoards(state, numberOfDays, city)
            console.log('dayboards loaded places length: ', Object.keys(dayBoardsLoaded.places).length)
            return dayBoardsLoaded
        case 'LOAD_DAYS':
            const { itinerary, placesFetched } = action.payload
            const daysLoaded = loadDays(state, itinerary, placesFetched)
            console.log(daysLoaded)
            return daysLoaded
        case 'LOAD_ITINERARY':
            const { extraSuggestions, placeType} = action.payload
            const itineraryLoaded = loadItinerary(state, extraSuggestions, placeType)
            console.log("complete itinerary: ", itineraryLoaded)
            return itineraryLoaded;
        case 'CLEAR_STATE':
            console.log('clearing state...')
            console.log('cleared state: ', initialData)
            const clearedState = clearState()
            //WHY can't i return initialData?
            return clearedState;
        case 'ADD_SEARCH_ITEM':
            const { placeObject } = action.payload
            const addedItem = addItem(state, placeObject)
            //console.log('hello from reducer: ', placeObject)
            console.log('reducer: ', addedItem);
            return addedItem;
        default:
            return state;
    }
}

const clearState = () => {
    const newState = {
        itineraryId: "",
        places: {},
        days: 0,
        location: "Berlin",
        columns: {
            'searched-items': {
                id:'searched-items',
                title: 'Searched Items',
                placeIds: []
            }
        },
        categoryBoards: [],
        dayBoards: [],
        searchBoard: ['searched-items']
    }

    return newState;
}

const addItem = (state, placeObject) => {
    const newState = {
        ...state,
        places: {
            ...state.places,
            [placeObject.id]: placeObject
        },
        columns: {
            ...state.columns,
            'searched-items': {
                ...state.columns['searched-items'],
                placeIds: [...state.columns['searched-items'].placeIds, placeObject.id]
            }
        }
    }

    return newState
}

const loadDayBoards = (state, numberOfDays, city) => {
    let columns = {};
    let dayBoards = [];

    for (var i = 0; i < numberOfDays; i++) {
        let dayObject = {};
        dayObject['id'] = `day-${i}`;
        dayObject['title'] = `Day ${i + 1}`;
        dayObject['placeIds'] = [];

        columns[`day-${i}`] = dayObject;
        dayBoards.push(`day-${i}`);
    }

    const newState = {
        ...state,
        days: numberOfDays,
        location: city,
        columns: {
            ...state.columns,
            ...columns,
        },
        dayBoards: [...dayBoards],
    }

    return newState
}

const loadDays = (state, itinerary, placesFetched) => {
    let columns = {};
    let dayBoards = [];

    const dayPlans = itinerary.dayPlans;

    for (var i = 0; i < dayPlans.length; i++) {
        let dayObject = {};
        dayObject['id'] = `day-${i}`;
        dayObject['title'] = `Day ${i + 1}`;
        dayObject['placeIds'] = [];

        columns[`day-${i}`] = dayObject;
        dayBoards.push(`day-${i}`);
    }


    for(var i = 0; i < dayPlans.length; i++) {
        const placeIds = dayPlans[i].placeIds
        columns[`day-${i}`].placeIds = placeIds
    }

    const newState = {
        ...state,
        itineraryId: itinerary.id,
        days: dayPlans.length,
        location: itinerary.city,
        places: placesFetched,
        columns: {
            ...state.columns,
            ...columns,
        },
        dayBoards: [...dayBoards],
    }

    return newState
}

const loadItinerary = (state, extraSuggestions, placeType) => {

    //console.log('hello from reducer: ', extraSuggestions)
    let placesFetched = state.places
    let columns = {};
    let categoryBoards = [];    

    let data = extraSuggestions;
    let placeIds = [];

    console.log("check data result length: ", data.results)

    for (var j = 0; j < data.results.length; j++) {

        //remove duplicate data from category boards
        if(data.results[j].place_id in placesFetched){
            continue;
        }

        var placeObject = {};
        placeObject['id'] = data.results[j].place_id;
        placeObject['content'] = data.results[j].name;
        placeObject['rating'] = data.results[j].rating;
        placeObject['photoRef'] = data.results[j].photos ? data.results[j].photos[0].photo_reference : "0";
        placeObject['location'] = data.results[j].geometry.location;

        //console.log(placeObject);
        placeIds.push(data.results[j].place_id);
        placesFetched[data.results[j].place_id] = placeObject;

    }



    columns[`category-${placeType}`] = {
        id: `category-${placeType}`,
        title: `${placeType}`,
        placeIds: placeIds
    }

    categoryBoards.push(`category-${placeType}`);


    const newState = {
        ...state,
        places: placesFetched,
        columns: {
            ...state.columns,
            ...columns,
        },
        categoryBoards: [...state.categoryBoards, ...categoryBoards],
    }

    console.log(newState)
    return newState;
}

const addExtraDay = (state) => {

    const nextColumnIndex = state.dayBoards.length;
    const nextColumn = {
        id: `day-${nextColumnIndex}`, 
        title: `Day ${nextColumnIndex + 1}`, 
        placeIds: []
    }

    const newState = {
        ...state,
        days: nextColumnIndex + 1,
        columns: {
            ...state.columns,
            [`day-${nextColumnIndex}`]: nextColumn
        },
        dayBoards: [...state.dayBoards, `day-${nextColumnIndex}`],
    }

    console.log(newState);
    return newState
}

const loadPlaces = (state, compiledData, days, location) => {

    let columns = {};
    let dayBoards = [];
    let categoryBoards = [];

    for (var i = 0; i < days; i++) {
        let dayObject = {};
        dayObject['id'] = `day-${i}`;
        dayObject['title'] = `Day ${i + 1}`;
        dayObject['placeIds'] = [];

        columns[`day-${i}`] = dayObject;
        dayBoards.push(`day-${i}`);
    }

    let placesFetched = {};
    const placeTypes = ['Restaurants', "Hotels", "Tourist+attraction"];

    for (var c = 0; c < compiledData.length; c++){

        let data = compiledData[c];
        let placeIds = [];

        for (var j = 0; j < data.results.length; j++) {

            //object structure of place card is set here

            var placeObject = {};
            placeObject['id'] = data.results[j].place_id;
            placeObject['content'] = data.results[j].name;
            placeObject['rating'] = data.results[j].rating;
            placeObject['photoRef'] = data.results[j].photos ? data.results[j].photos[0].photo_reference : "0";
            placeObject['location'] = data.results[j].geometry.location;

            //console.log(placeObject);
            placeIds.push(data.results[j].place_id);
            placesFetched[data.results[j].place_id] = placeObject;

        }

        columns[`category-${c+1}`] = {
            id: `category-${c+1}`,
            title: `${placeTypes[c]}`,
            placeIds: placeIds
        }

        categoryBoards.push(`category-${c+1}`);

    } 

    const newState = {
        ...state,
        days: days,
        location: location,
        places: {
            ...state.places,
            ...placesFetched,
        },
        columns: {
            ...state.columns,
            ...columns,
        },
        dayBoards: [...dayBoards],
        categoryBoards: [...categoryBoards],
    }

    //console.log(newState);
    return newState;
}
