import React, { useContext } from 'react';
import { Link } from 'react-router-dom';
import { useQuery } from '@apollo/react-hooks';
import gql from 'graphql-tag';

import { AuthContext } from '../Store/AuthContext';

function Itineraries() {

    const { authState } = useContext(AuthContext);
    const {
        loading,
        data: { getUserItineraries : itineraries } = {}
    } = useQuery(GET_USER_ITINERARIES);

    console.log(itineraries);
    let itineraryCards;
    if (itineraries) {
        itineraryCards = itineraries.map((itinerary) => {
        return <Link to={`/itinerary/${itinerary.id}`} key={itinerary.id}><p>{itinerary.city}: {itinerary.id}</p></Link>
        })
    }

    return (
        <div>
            <h1>Itineraries</h1>
            {itineraryCards}
        </div>
    );
}

const GET_USER_ITINERARIES = gql`
    query{
        getUserItineraries{
            id
            city
            dayPlans{
                placeIds
            }
            createdAt
            user
            username
        }
    }
`

export default Itineraries;
