import React, {useState} from 'react';
import { Draggable } from 'react-beautiful-dnd';

import { makeStyles } from "@material-ui/core/styles";
import {ExpansionPanel, ExpansionPanelSummary, ExpansionPanelDetails, Typography} from '@material-ui/core';
import { grey, white} from "@material-ui/core/colors";

import Accordion from 'react-bootstrap/Accordion';
import Card from 'react-bootstrap/Card';

const useStyles = makeStyles({
    panel: {
        margin: "5px",
        minWidth: "200px",
        maxWidth: "320px",
    },
    panelHeader: {
        backgroundColor: "grey",
        color: "white",
    },
    panelBody: {
        maxHeight: "200px",
        overflowY: "scroll",
    }

});


function Item(props) {

    const {place, index} = props;
    //const placeImgUrl = "/place/photo?maxheight=400&photoreference=" + place.photoRef + "&key=" + process.env.REACT_APP_GOOGLE_PLACES_API_KEY; 

    const classes = useStyles();

    const [expanded, setExpanded] = useState('panel1');

    const handleChange = (panel) => (event, newExpanded) => {
        setExpanded(newExpanded ? panel : false);
    };

    return (
        <Draggable draggableId={place.id} 
            index={index}
        >
            {(provided) => (
                <ExpansionPanel
                    square
                    className="item"
                    expanded={expanded === 'panel1'} 
                    onChange={handleChange('panel1')}
                    ref={provided.innerRef}
                    {...provided.draggableProps}
                    {...provided.dragHandleProps}
                >
                    <ExpansionPanelSummary className={classes.panelHeader}>
                        <Typography>{index+1}. {place.content}</Typography>
                    </ExpansionPanelSummary>
                    <ExpansionPanelDetails className={classes.panelBody}>
                        <Typography>
                            <img alt="" src="https://i.imgur.com/zbBglmB.jpg"/>
                            <p>{place.rating}</p>
                            <p>Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur. Excepteur sint occaecat cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id est laborum.</p>
                        </Typography>
                    </ExpansionPanelDetails>
                </ExpansionPanel>
            )}
        </Draggable>
    );
}

export default Item;

