import React, {useState} from 'react';

function PlaceAutoComplete() {

    const [searchState, setSearchState] = useState("");
    const [sugestionsState, setSuggestionState] = useState([]);

    const googlePlacesApi = process.env.REACT_APP_GOOGLE_PLACES_API_KEY;


    const searchHandler = async (e) => {
        setSearchState(e.target.value)
        const response = await fetch(`place/autocomplete/json?input=${e.target.value}&types=establishment&location=37.76999,-122.44696&radius=500&key=${googlePlacesApi}`)
        const data = await response.json();
        setSuggestionState(data.predictions);
        console.log(data);
    }

    return(
        <div>
            <input value={searchState} onChange={searchHandler}/>
            {sugestionsState.map((suggestion) => {
                return <p key={suggestion.place_id}>{suggestion.description}</p>
            })}
        </div>

    );
}

export default PlaceAutoComplete;
